import React, { useContext } from 'react';
import { List, ListItem, ListItemText, IconButton, Typography, Divider, Paper } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { SalesContext } from './SalesContext';

function SaleList() {
    const { sales, startEdit, deleteSale } = useContext(SalesContext);

    // Function to group and sort sales by date
    const groupedSales = sales.reduce((acc, sale) => {
        const date = sale.date;
        acc[date] = acc[date] || [];
        acc[date].push(sale);
        return acc;
    }, {});

    const sortedDates = Object.keys(groupedSales).sort((a, b) => new Date(b) - new Date(a));

    return (
        <List sx={{ width: '100%', mt: 4 }}>
            {sortedDates.map(date => (
                <React.Fragment key={date}>
                    <Paper elevation={2} sx={{ my: 2, p: 2 }}>
                        <Typography variant="h6" color="primary" sx={{ mb: 2 }}>
                            Sales on {date}
                        </Typography>
                        <Divider sx={{ mb: 2 }} />
                        {groupedSales[date].map(sale => (
                        <ListItem 
                            key={sale.id} 
                            secondaryAction={
                                <>
                                    <IconButton onClick={() => startEdit(sale)}><EditIcon /></IconButton>
                                    <IconButton onClick={() => deleteSale(sale.id)}><DeleteIcon /></IconButton>
                                </>
                            }
                            sx={{ mb: 1 }}
                        >
                        <ListItemText
                            primary={`${sale.itemName} - $${sale.price}`}
                        secondary={`Category: ${sale.category} | Date: ${sale.date} | Payment Mode: ${sale.paymentMode}`}
                        />
                        </ListItem>
                    ))}

                    </Paper>
                </React.Fragment>
            ))}
        </List>
    );
}

export default SaleList;
