import React, { useContext } from 'react';
import { List, ListItem, ListItemText, IconButton, Typography, Divider } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { SalesContext } from './SalesContext';

function SaleList() {
    const { sales, startEdit, deleteSale } = useContext(SalesContext);

    // Grouping sales by date and sorting them by the latest date first
    const groupedSales = sales.reduce((acc, sale) => {
        const date = sale.date;
        if (!acc[date]) {
            acc[date] = [];
        }
        acc[date].push(sale);
        return acc;
    }, {});

    const sortedDates = Object.keys(groupedSales).sort((a, b) => new Date(b) - new Date(a));

    return (
        <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
            {sortedDates.map(date => (
                <React.Fragment key={date}>
                    <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>
                        Sales on {date}
                    </Typography>
                    <Divider />
                    {groupedSales[date].map((sale) => (
                        <ListItem 
                            key={sale.id} 
                            secondaryAction={
                                <>
                                    <IconButton edge="end" aria-label="edit" onClick={() => startEdit(sale)}>
                                        <EditIcon />
                                    </IconButton>
                                    <IconButton edge="end" aria-label="delete" onClick={() => deleteSale(sale.id)}>
                                        <DeleteIcon />
                                    </IconButton>
                                </>
                            }
                        >
                            <ListItemText
                                primary={`${sale.itemName} - $${sale.price}`}
                                secondary={`Payment Mode: ${sale.paymentMode}`}
                            />
                        </ListItem>
                    ))}
                </React.Fragment>
            ))}
        </List>
    );
}

export default SaleList;
