import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]);

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        try {
            const result = await axios('http://localhost:8080/sales');
            setSales(result.data);
        } catch (error) {
            console.error("Error fetching sales:", error);
        }
    };

    const addSale = async () => {
        const newSale = { itemName: "New Item", price: 19.99, date: "2023-04-17", amount: 2, paymentMode: "cash" };
        try {
            await axios.post('http://localhost:8080/sales', newSale);
            fetchSales();
        } catch (error) {
            console.error("Error adding sale:", error);
        }
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <button onClick={addSale}>Add Sale</button>
            <ul>
                {sales.map((sale) => (
                    <li key={sale.id}>{sale.itemName} - ${sale.price}</li>
                ))}
            </ul>
        </div>
    );
}

export default App;
