import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:8080/sales');
            setSales(response.data);
            setError(null);
        } catch (err) {
            setError('Error fetching sales');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const addSale = async () => {
        const newSale = { itemName: "New Item", price: 100, date: new Date().toISOString().split('T')[0], amount: 1, paymentMode: "card" };
        try {
            await axios.post('http://localhost:8080/sales', newSale);
            fetchSales();
        } catch (err) {
            console.error('Error adding sale', err);
        }
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <button onClick={addSale} disabled={loading}>{loading ? 'Adding...' : 'Add Sale'}</button>
            {error && <p>{error}</p>}
            <ul>
                {sales.map(sale => (
                    <li key={sale.id}>{sale.itemName} - ${sale.price} - {sale.date}</li>
                ))}
            </ul>
        </div>
    );
}

export default App;
