import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const SalesContext = createContext();

export const SalesProvider = ({ children }) => {
    const [sales, setSales] = useState([]); // Initialize as empty array

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        try {
            const response = await axios.get('http://localhost:8080/sales');
            setSales(response.data);
        } catch (error) {
            console.error('Failed to fetch sales', error);
            setSales([]); // Ensure sales is set to an empty array on error
        }
    };

    // Other context methods...

    return (
        <SalesContext.Provider value={{ sales, /* other values */ }}>
            {children}
        </SalesContext.Provider>
    );
};
