import React, { useContext } from 'react';
import { List, ListItem, ListItemText, IconButton } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { SalesContext } from './SalesContext';

function SaleList() {
    const { sales, startEdit, deleteSale } = useContext(SalesContext);

    return (
        <List>
            {sales.map((sale) => (
                <ListItem key={sale.id} secondaryAction={
                    <>
                        <IconButton edge="end" aria-label="edit" onClick={() => startEdit(sale)}>
                            <EditIcon />
                        </IconButton>
                        <IconButton edge="end" aria-label="delete" onClick={() => deleteSale(sale.id)}>
                            <DeleteIcon />
                        </IconButton>
                    </>
                }>
                    <ListItemText
                        primary={`${sale.itemName} - $${sale.price}`}
                        secondary={`Date: ${sale.date} | Payment Mode: ${sale.paymentMode}`}
                    />
                </ListItem>
            ))}
        </List>
    );
}

export default SaleList;
