import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [formData, setFormData] = useState({
        itemName: '',
        price: '',
        date: '',
        paymentMode: 'cash'
    });
    const [edit, setEdit] = useState(false);
    const [currentSaleId, setCurrentSaleId] = useState(null);

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:8080/sales');
            setSales(response.data);
        } catch (err) {
            setError('Failed to fetch sales');
        } finally {
            setLoading(false);
        }
    };

    const handleFormChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const addOrUpdateSale = async () => {
        if (edit) {
            try {
                await axios.put(`http://localhost:8080/sales/${currentSaleId}`, formData);
                setEdit(false);
                setCurrentSaleId(null);
            } catch (err) {
                setError('Failed to update sale');
            }
        } else {
            try {
                await axios.post('http://localhost:8080/sales', formData);
            } catch (err) {
                setError('Failed to add sale');
            }
        }
        setFormData({
            itemName: '',
            price: '',
            date: '',
            paymentMode: 'cash'
        });
        fetchSales();
    };

    const startEdit = (sale) => {
        setEdit(true);
        setCurrentSaleId(sale.id);
        setFormData({
            itemName: sale.itemName,
            price: sale.price,
            date: sale.date,
            paymentMode: sale.paymentMode
        });
    };

    const deleteSale = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/sales/${id}`);
            fetchSales();
        } catch (err) {
            setError('Failed to delete sale');
        }
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <input type="text" name="itemName" placeholder="Item Name" value={formData.itemName} onChange={handleFormChange} />
            <input type="number" name="price" placeholder="Price" value={formData.price} onChange={handleFormChange} />
            <input type="date" name="date" value={formData.date} onChange={handleFormChange} />
            <select name="paymentMode" value={formData.paymentMode} onChange={handleFormChange}>
                <option value="cash">Cash</option>
                <option value="card">Card</option>
            </select>
            <button onClick={addOrUpdateSale}>{edit ? 'Update Sale' : 'Add Sale'}</button>
            {error && <p>{error}</p>}
            <ul>
                {sales.map(sale => (
                    <li key={sale.id}>
                        {sale.itemName} - ${sale.price} - {sale.date} - {sale.paymentMode}
                        <button onClick={() => startEdit(sale)}>Edit</button>
                        <button onClick={() => deleteSale(sale.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
