import React, { useContext } from 'react';
import { TextField, Button, FormControl, InputLabel, Select, MenuItem, Box, Grid, Paper } from '@mui/material';
import { SalesContext } from './SalesContext';

function SaleForm() {
    const { formData, setFormData, addOrUpdateSale } = useContext(SalesContext);
    const handleFormChange = (event) => {
        setFormData({ ...formData, [event.target.name]: event.target.value });
    };

    return (
        <Paper style={{ padding: 16, marginBottom: 32 }}>
            <Grid container alignItems="flex-start" spacing={2}>
                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Item Name"
                        name="itemName"
                        value={formData.itemName}
                        onChange={handleFormChange}
                        variant="outlined"
                    />
                </Grid>
                <Grid item xs={12} sm={3}>
                    <TextField
                        fullWidth
                        label="Price"
                        type="number"
                        name="price"
                        value={formData.price}
                        onChange={handleFormChange}
                        variant="outlined"
                    />
                </Grid>
                <Grid item xs={12} sm={3}>
                    <TextField
                        fullWidth
                        label="Date"
                        type="date"
                        name="date"
                        InputLabelProps={{ shrink: true }}
                        value={formData.date}
                        onChange={handleFormChange}
                        variant="outlined"
                    />
                </Grid>
                <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                        <InputLabel>Payment Mode</InputLabel>
                        <Select
                            name="paymentMode"
                            value={formData.paymentMode}
                            onChange={handleFormChange}
                            variant="outlined"
                        >
                            <MenuItem value="cash">Cash</MenuItem>
                            <MenuItem value="card">Card</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                    <Button
                        fullWidth
                        variant="contained"
                        color="primary"
                        onClick={addOrUpdateSale}
                        size="large"
                    >
                        {formData.editing ? 'Update Sale' : 'Add Sale'}
                    </Button>
                </Grid>
            </Grid>
        </Paper>
    );
}

export default SaleForm;
