import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const SalesContext = createContext(null);

export const SalesProvider = ({ children }) => {
    const [sales, setSales] = useState([]);
    const [formData, setFormData] = useState({
        itemName: '',
        price: '',
        date: '',
        paymentMode: 'cash',
        editing: false,
        id: null
    });

    useEffect(() => {
        const fetchSales = async () => {
            try {
                const response = await axios.get('http://localhost:8080/sales');
                setSales(response.data);
            } catch (error) {
                console.error('Failed to fetch sales', error);
            }
        };
        fetchSales();
    }, []);

    const fetchSales = async () => {
        try {
            const response = await axios.get('http://localhost:8080/sales');
            setSales(response.data);
        } catch (error) {
            console.error('Failed to fetch sales', error);
        }
    };

    const addOrUpdateSale = async () => {
        const method = formData.editing ? 'put' : 'post';
        const url = formData.editing ? `http://localhost:8080/sales/${formData.id}` : 'http://localhost:8080/sales';
        try {
            await axios[method](url, {
                itemName: formData.itemName,
                price: parseFloat(formData.price), // Ensure price is a float
                date: formData.date,
                paymentMode: formData.paymentMode
            });
            fetchSales();
        } catch (error) {
            console.error('Failed to add/update sale', error);
        }
        setFormData({ itemName: '', price: '', date: '', paymentMode: 'cash', editing: false, id: null }); // Reset form data
    };

    const startEdit = (sale) => {
        setFormData({
            ...sale,
            editing: true,
            price: sale.price.toString() // Convert price back to string for form handling
        });
    };

    const deleteSale = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/sales/${id}`);
            fetchSales();
        } catch (error) {
            console.error('Failed to delete sale', error);
        }
    };

    return (
        <SalesContext.Provider value={{ sales, formData, setFormData, addOrUpdateSale, startEdit, deleteSale }}>
            {children}
        </SalesContext.Provider>
    );
};
