import React, { useContext } from 'react';
import { List, ListItem, ListItemText, IconButton, Typography, Divider, Paper, Button } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { SalesContext } from './SalesContext';
import axios from 'axios';
import { saveAs } from 'file-saver';

function SaleList() {
    const { sales, startEdit, deleteSale } = useContext(SalesContext);

    const exportSales = () => {
        axios({
            url: 'http://localhost:8080/export-sales',
            method: 'GET',
            responseType: 'blob',
        })
        .then((response) => {
            saveAs(new Blob([response.data]), 'sales-report.pdf');
        })
        .catch(error => {
            console.error("There was an error exporting the file: ", error);
        });
    };

    if (!sales || sales.length === 0) return <Typography>No sales data available.</Typography>;

    return (
        <div>
            <Button variant="contained" color="primary" onClick={exportSales} sx={{ mb: 2 }}>
                Export to PDF
            </Button>
            <List sx={{ width: '100%', mt: 1 }}>
                {sales.map(sale => (
                    <React.Fragment key={sale.id}>
                        <Paper elevation={2} sx={{ my: 2, p: 2 }}>
                            <Typography variant="h6" color="primary" sx={{ mb: 2 }}>
                                Sales on {sale.date} | Cash Sales: ${sale.totals.cash.toFixed(2)} | Card Sales: ${sale.totals.card.toFixed(2)}
                            </Typography>
                            <Divider sx={{ mb: 2 }} />
                            <ListItem 
                                secondaryAction={
                                    <>
                                        <IconButton onClick={() => startEdit(sale)}><EditIcon /></IconButton>
                                        <IconButton onClick={() => deleteSale(sale.id)}><DeleteIcon /></IconButton>
                                    </>
                                }
                                sx={{ mb: 1 }}
                            >
                                <ListItemText
                                    primary={`${sale.itemName} - $${sale.price}`}
                                    secondary={`Category: ${sale.category} | Date: ${sale.date} | Payment Mode: ${sale.paymentMode}`}
                                />
                            </ListItem>
                        </Paper>
                    </React.Fragment>
                ))}
            </List>
        </div>
    );
}

export default SaleList;
