import React, { useContext } from 'react';
import { SalesContext } from './SalesContext';
import { Typography, Box } from '@mui/material';

function TodaySalesSummary() {
    const { todaySalesSummary } = useContext(SalesContext);

    if (!todaySalesSummary || todaySalesSummary.length === 0) {
        return <Typography>Loading sales summary...</Typography>;
    }

    return (
        <Box sx={{ mt: 2 }}>
            <Typography variant="h6">Today's Sales Summary</Typography>
            {todaySalesSummary.length > 0 ? (
                todaySalesSummary.map((item) => (
                    <Typography key={item.paymentMode}>
                        {`Total ${item.paymentMode}: $${item.total.toFixed(2)}`}
                    </Typography>
                ))
            ) : (
                <Typography>No sales data for today.</Typography>
            )}
        </Box>
    );
}

export default TodaySalesSummary;
