import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [formData, setFormData] = useState({
        itemName: '',
        price: '',
        date: '',
        paymentMode: 'cash'  // Default payment mode
    });

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:8080/sales');
            if (Array.isArray(response.data)) {
                setSales(response.data);
            } else {
                throw new Error('Data is not an array');
            }
        } catch (err) {
            setError('Error fetching sales');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const addSale = async () => {
        try {
            const response = await axios.post('http://localhost:8080/sales', {
                itemName: formData.itemName,
                price: parseFloat(formData.price),
                date: formData.date,
                paymentMode: formData.paymentMode
            });
            if (response.status === 201) {
                fetchSales();
                setFormData({
                    itemName: '',
                    price: '',
                    date: '',
                    paymentMode: 'cash'
                });
            }
        } catch (err) {
            console.error('Error adding sale', err);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <div>
                <input
                    type="text"
                    name="itemName"
                    placeholder="Item Name"
                    value={formData.itemName}
                    onChange={handleChange}
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Price"
                    value={formData.price}
                    onChange={handleChange}
                />
                <input
                    type="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                />
                <select
                    name="paymentMode"
                    value={formData.paymentMode}
                    onChange={handleChange}
                >
                    <option value="cash">Cash</option>
                    <option value="card">Card</option>
                </select>
                <button onClick={addSale} disabled={loading}>
                    Add Sale
                </button>
            </div>
            {error && <p>{error}</p>}
            <ul>
                {sales.map(sale => (
                    <li key={sale.id}>{sale.itemName} - ${sale.price} - {sale.date} - {sale.paymentMode}</li>
                ))}
            </ul>
        </div>
    );
}

export default App;
