import React, { useContext } from 'react';
import { TextField, Button, FormControl, InputLabel, Select, MenuItem, Box } from '@mui/material';
import { SalesContext } from './SalesContext';

function SaleForm() {
    const { formData, setFormData, addOrUpdateSale } = useContext(SalesContext);

    const handleFormChange = (event) => {
        const { name, value } = event.target;
        const isPriceField = name === 'price';
        setFormData({
            ...formData,
            [name]: isPriceField ? parseFloat(value) || 0 : value  // Convert price to float, default to 0 if NaN
        });
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, marginTop: 4 }}>
            <TextField
                label="Item Name"
                variant="outlined"
                name="itemName"
                value={formData.itemName}
                onChange={handleFormChange}
                fullWidth
            />
            <TextField
                label="Price"
                variant="outlined"
                name="price"
                type="number"
                value={formData.price.toString()}  // Ensure the input is always treated as text
                onChange={handleFormChange}
                fullWidth
            />
            <TextField
                label="Date"
                type="date"
                variant="outlined"
                name="date"
                InputLabelProps={{ shrink: true }}
                value={formData.date}
                onChange={handleFormChange}
                fullWidth
            />
            <FormControl fullWidth>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                    labelId="category-label"
                    name="category"
                    value={formData.category}
                    label="Category"
                    onChange={handleFormChange}
                >
                    <MenuItem value="Clothes">Clothes</MenuItem>
                    <MenuItem value="Jewelry">Jewelry</MenuItem>
                    <MenuItem value="Bag">Bag</MenuItem>
                    <MenuItem value="Stationary">Stationary</MenuItem>
                    {/* Add more categories as needed */}
                </Select>
            </FormControl>

            <FormControl fullWidth>
                <InputLabel>Payment Mode</InputLabel>
                <Select
                    name="paymentMode"
                    value={formData.paymentMode}
                    label="Payment Mode"
                    onChange={handleFormChange}
                >
                    <MenuItem value="cash">Cash</MenuItem>
                    <MenuItem value="card">Card</MenuItem>
                </Select>
            </FormControl>
            <Button variant="contained" color="primary" onClick={addOrUpdateSale} sx={{ alignSelf: 'center' }}>
                {formData.editing ? 'Update Sale' : 'Add Sale'}
            </Button>
        </Box>
    );
}

export default SaleForm;
