import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]); // Initialize sales as an empty array
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        setLoading(true);
        setError(null);
        try {
            const result = await axios('http://localhost:8080/sales');
            setSales(result.data);  // Expecting result.data to be an array
            setLoading(false);
        } catch (error) {
            console.error("Error fetching sales:", error);
            setError('Failed to fetch sales');
            setLoading(false);
            setSales([]);  // Set to empty array in case of error
        }
    };

    const addSale = async () => {
        const newSale = { itemName: "New Item", price: 19.99, date: "2023-04-17", amount: 2, paymentMode: "cash" };
        try {
            await axios.post('http://localhost:8080/sales', newSale);
            fetchSales(); // Refresh the list after adding
        } catch (error) {
            console.error("Error adding sale:", error);
            alert('Failed to add sale');
        }
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <button onClick={addSale} disabled={loading}>Add Sale</button>
            {error && <p>{error}</p>}
            <ul>
                {sales.map((sale) => (
                    <li key={sale.id}>
                        {sale.itemName} - ${sale.price}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
