import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [sales, setSales] = useState([]);

    useEffect(() => {
        fetchSales();
    }, []);

    const fetchSales = async () => {
        const result = await axios('http://localhost:8080/sales');
        setSales(result.data);
    };

    const addSale = async () => {
        const newSale = { itemName: "Example Item", price: 9.99, date: "2023-04-12", amount: 1, paymentMode: "card" };
        await axios.post('http://localhost:8080/sales', newSale);
        fetchSales();
    };

    return (
        <div>
            <h1>Sales Tracker</h1>
            <button onClick={addSale}>Add Sale</button>
            <ul>
                {sales.map(sale => (
                    <li key={sale.id}>{sale.itemName} - ${sale.price}</li>
                ))}
            </ul>
        </div>
    );
}

export default App;
